import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router, RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { BlogComment } from '../../models/comment.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-post-detail',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './post-detail.component.html',
  styleUrls: ['./post-detail.component.css']
})
export class PostDetailComponent implements OnInit {
  post$: Observable<Post | undefined>;
  newComment: BlogComment = { id: 0, content: '', author: '', createdAt: '' };

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private postService: PostService
  ) {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.post$ = this.postService.getPostById(id);
  }

  ngOnInit(): void {
    // No manual subscription needed; async pipe handles it
  }

  toggleLike(postId: number): void {
    this.postService.toggleLike(postId);
  }

  toggleFavorite(postId: number): void {
    this.postService.toggleFavorite(postId);
  }

  addComment(postId: number): void {
    if (this.newComment.content && this.newComment.author) {
      this.postService.addComment(postId, this.newComment);
      this.newComment = { id: 0, content: '', author: '', createdAt: '' };
    }
  }

  goBack(): void {
    this.router.navigate(['/']);
  }

  trackById(index: number, comment: BlogComment): number {
    return comment.id;
  }
}
