import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router, RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-post-form',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './post-form.component.html',
  styleUrls: ['./post-form.component.css']
})
export class PostFormComponent implements OnInit {
  post: Post = { 
    id: 0, 
    title: '', 
    content: '', 
    imageUrl: '', 
    author: '', 
    createdAt: '', 
    category: 'Peinture', 
    likes: 0, 
    isLiked: false, 
    isFavorite: false, 
    comments: [] 
  };
  isEditMode = false;
  post$: Observable<Post | undefined> | undefined;
  showShareOptions = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private postService: PostService
  ) {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.isEditMode = true;
      this.post$ = this.postService.getPostById(+id);
    }
  }

  ngOnInit(): void {
    if (this.post$ && this.isEditMode) {
      this.post$.subscribe(post => {
        if (post) {
          this.post = { ...post };
        }
      });
    }
  }

  savePost(): void {
    if (this.post.title && this.post.content && this.post.imageUrl && this.post.author && this.post.category) {
      if (this.isEditMode) {
        this.postService.updatePost(this.post.id, this.post);
      } else {
        this.post.createdAt = new Date().toISOString();
        this.postService.addPost(this.post);
      }
      this.router.navigate(['/']);
    }
  }

  cancel(): void {
    this.router.navigate(['/']);
  }

  toggleShareOptions(): void {
    this.showShareOptions = !this.showShareOptions;
  }

  share(platform: string): void {
    const url = window.location.origin + '/post/' + (this.post.id || 'new');
    const text = encodeURIComponent(this.post.title);
    let shareUrl = '';

    switch (platform) {
      case 'twitter':
        shareUrl = `https://twitter.com/intent/tweet?url=${url}&text=${text}`;
        break;
      case 'facebook':
        shareUrl = `https://www.facebook.com/sharer/sharer.php?u=${url}`;
        break;
      case 'linkedin':
        shareUrl = `https://www.linkedin.com/shareArticle?mini=true&url=${url}&title=${text}`;
        break;
    }

    window.open(shareUrl, '_blank', 'width=600,height=400');
    this.showShareOptions = false;
  }
}
