import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';

@Component({
  selector: 'app-post-form',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    <div class="form-container">
      <h1>{{ isEditMode ? 'Modifier l\'article' : 'Nouvel article' }}</h1>
      <form (submit)="savePost()">
        <div class="form-group">
          <label for="title">Titre</label>
          <input 
            type="text" 
            id="title" 
            [(ngModel)]="post.title" 
            name="title" 
            required
          >
        </div>
        <div class="form-group">
          <label for="content">Contenu</label>
          <textarea 
            id="content" 
            [(ngModel)]="post.content" 
            name="content" 
            required
          ></textarea>
        </div>
        <div class="form-group">
          <label for="imageUrl">URL de l'image</label>
          <input 
            type="text" 
            id="imageUrl" 
            [(ngModel)]="post.imageUrl" 
            name="imageUrl" 
            required
          >
        </div>
        <div class="form-group">
          <label for="category">Catégorie</label>
          <select 
            id="category" 
            [(ngModel)]="post.category" 
            name="category" 
            required
          >
            <option value="Peinture">Peinture</option>
            <option value="Sculpture">Sculpture</option>
            <option value="Photographie">Photographie</option>
            <option value="Art Numérique">Art Numérique</option>
          </select>
        </div>
        <div class="form-group">
          <label for="author">Votre nom</label>
          <input 
            type="text" 
            id="author" 
            [(ngModel)]="post.author" 
            name="author" 
            required
          >
        </div>
        <div class="form-actions">
          <button type="submit" class="save-btn">
            {{ isEditMode ? 'Mettre à jour' : 'Créer' }}
          </button>
          <button type="button" class="cancel-btn" (click)="cancel()">
            Annuler
          </button>
        </div>
      </form>
    </div>
  `,
  styles: [`
    .form-container {
      max-width: 600px;
      margin: 40px auto;
      padding: 30px;
      background: white;
      border-radius: 12px;
      box-shadow: 0 4px 15px rgba(0,0,0,0.1);
    }

    h1 {
      font-family: 'Playfair Display', serif;
      font-size: 2rem;
      color: #2C3E50;
      margin-bottom: 30px;
      text-align: center;
    }

    .form-group {
      margin-bottom: 20px;
    }

    label {
      display: block;
      margin-bottom: 5px;
      color: #2C3E50;
      font-weight: 500;
    }

    input, textarea, select {
      width: 100%;
      padding: 10px;
      border: 1px solid #ddd;
      border-radius: 5px;
      font-family: inherit;
    }

    textarea {
      height: 150px;
      resize: vertical;
    }

    select {
      appearance: none;
      background: url('data:image/svg+xml;utf8,<svg fill="%23333333" height="24" viewBox="0 0 24 24" width="24" xmlns="http://www.w3.org/2000/svg"><path d="M7 10l5 5 5-5z"/></svg>') no-repeat right 10px center;
      background-size: 12px;
    }

    .form-actions {
      display: flex;
      gap: 15px;
      justify-content: center;
    }

    .save-btn, .cancel-btn {
      padding: 12px 24px;
      border-radius: 25px;
      cursor: pointer;
      transition: all 0.3s ease;
    }

    .save-btn {
      background: #D4A017;
      color: white;
      border: none;
    }

    .save-btn:hover {
      background: #b88b14;
    }

    .cancel-btn {
      background: #E74C3C;
      color: white;
      border: none;
    }

    .cancel-btn:hover {
      background: #c0392b;
    }
  `]
})
export class PostFormComponent implements OnInit {
  post: Post = { id: 0, title: '', content: '', imageUrl: '', author: '', createdAt: '', category: 'Peinture', likes: 0, isLiked: false, isFavorite: false, comments: [] };
  isEditMode = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private postService: PostService
  ) {}

  ngOnInit() {
    this.route.params.subscribe(params => {
      const id = params['id'];
      if (id) {
        this.isEditMode = true;
        this.postService.getPostById(+id).subscribe(post => {
          if (post) {
            this.post = { ...post };
          }
        });
      }
    });
  }

  savePost() {
    if (this.isEditMode) {
      this.postService.updatePost(this.post.id, this.post);
    } else {
      this.postService.addPost(this.post);
    }
    this.router.navigate(['/']);
  }

  cancel() {
    this.router.navigate(['/']);
  }
}
