import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-favorites',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './favorites.component.html',
  styleUrls: ['./favorites.component.css']
})
export class FavoritesComponent implements OnInit {
  favorites$: Observable<Post[]>;
  defaultImage = 'https://via.placeholder.com/300x200?text=Image+non+disponible';

  constructor(private postService: PostService) {
    this.favorites$ = this.postService.getFavorites();
  }

  ngOnInit(): void {}

  toggleLike(postId: string): void { // Changé de number à string
    this.postService.toggleLike(postId);
  }

  toggleFavorite(postId: string): void { // Changé de number à string
    this.postService.toggleFavorite(postId);
  }

  trackById(index: number, post: Post): string { // Changé de number à string
    return post.id;
  }

  getImageUrl(imageUrl: string | undefined): string {
    return imageUrl || this.defaultImage;
  }
}
