import { Component, OnInit, OnDestroy } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { Observable, of, Subscription } from 'rxjs';
import { catchError } from 'rxjs/operators';

@Component({
  selector: 'app-admin',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit, OnDestroy {
  posts$: Observable<Post[]>;
  defaultImage = 'https://via.placeholder.com/300x200?text=Image+non+disponible';
  newPostNotification: string | null = null;
  private notificationSubscription: Subscription;

  constructor(private postService: PostService) {
    this.posts$ = this.postService.getPosts().pipe(
      catchError((error: unknown) => {
        console.error('Erreur lors du chargement des articles:', error);
        return of([]);
      })
    );

    // Subscribe to new post notifications
    this.notificationSubscription = this.postService.newPostNotification$.subscribe(
      message => {
        this.newPostNotification = message;
      }
    );
  }

  ngOnInit(): void {
    // Initialisation supplémentaire si nécessaire
  }

  ngOnDestroy(): void {
    // Unsubscribe to prevent memory leaks
    this.notificationSubscription.unsubscribe();
  }

  trackById(index: number, post: Post): number {
    return post.id;
  }

  getImageUrl(imageUrl: string | undefined): string {
    return `url(${imageUrl || this.defaultImage})`;
  }

  deletePost(id: number): void {
    if (confirm('Êtes-vous sûr de vouloir supprimer cet article ?')) {
      try {
        this.postService.deletePost(id); // Synchronous call, no return value
        // The posts$ Observable will update automatically via postsSubject
      } catch (error) {
        console.error('Erreur lors de la suppression:', error);
        alert('Une erreur est survenue lors de la suppression');
      }
    }
  }

  // Method to dismiss the notification
  dismissNotification(): void {
    this.newPostNotification = null;
    this.postService.clearNotification();
  }

  // Method to simulate adding a new post (for testing purposes)
  addTestPost(): void {
    const newPost: Post = {
      id: 0, // Will be set by addPost
      title: 'Nouvel article de test',
      content: 'Ceci est un article de test ajouté par l\'admin...',
      imageUrl: 'https://images.unsplash.com/photo-1519681393784-d120267933ba',
      author: 'Admin Test',
      createdAt: new Date().toISOString(),
      category: 'Test',
      likes: 0,
      isLiked: false,
      isFavorite: false,
      comments: []
    };
    this.postService.addPost(newPost);
  }
}
