import { Injectable } from '@angular/core';
import { HttpClient, HttpErrorResponse } from '@angular/common/http';
import { BehaviorSubject, Observable, of } from 'rxjs';
import { catchError, map, tap } from 'rxjs/operators';
import { Post } from '../models/post.model';
import { BlogComment } from '../models/comment.model';
import { NotificationService } from './notification.service';

@Injectable({
  providedIn: 'root'
})
export class PostService {
  private apiUrl = 'http://localhost:8080/api/posts';
  private commentsApiUrl = 'http://localhost:8080/api/comments';
  private postsSubject = new BehaviorSubject<Post[]>([]);
  private filteredPostsSubject = new BehaviorSubject<Post[]>([]);
  private favoritesSubject = new BehaviorSubject<Post[]>([]);

  constructor(
    private http: HttpClient,
    private notificationService: NotificationService
  ) {
    this.loadPosts();
  }

  private loadPosts(): void {
    this.http.get<Post[]>(this.apiUrl).pipe(
      catchError((error: HttpErrorResponse) => {
        console.error('Erreur lors du chargement des posts:', error);
        this.postsSubject.next([]);
        this.filteredPostsSubject.next([]);
        this.favoritesSubject.next([]);
        return of([]);
      })
    ).subscribe(posts => {
      this.postsSubject.next(posts);
      this.filteredPostsSubject.next(posts);
      this.favoritesSubject.next(this.getFavoritesSync());
    });
  }

  getPosts(): Observable<Post[]> {
    return this.filteredPostsSubject.asObservable();
  }

  getAllPosts(): Observable<Post[]> {
    return this.postsSubject.asObservable();
  }

  getPostById(id: string): Observable<Post | undefined> {
    return this.http.get<Post>(`${this.apiUrl}/${id}`).pipe(
      tap(post => {
        this.loadComments(post.id).subscribe(comments => {
          post.comments = comments;
          this.postsSubject.next([...this.postsSubject.getValue()]);
        });
      }),
      catchError((error: HttpErrorResponse) => {
        console.error(`Erreur lors de la récupération du post avec ID ${id}:`, error);
        return of(undefined);
      })
    );
  }

  async addPost(post: Post): Promise<void> {
    try {
      const newPost = await this.http.post<Post>(this.apiUrl, { ...post, authorId: '1' }).pipe(
        catchError((error: HttpErrorResponse) => {
          console.error('Erreur lors de l\'ajout du post:', error);
          throw error;
        })
      ).toPromise();
      if (newPost) {
        this.loadPosts(); // Rafraîchir la liste après ajout
      } else {
        throw new Error('Le post retourné est undefined');
      }
    } catch (error) {
      console.error('Erreur lors de l\'ajout:', error);
      throw error;
    }
  }

  async updatePost(id: string, updatedPost: Post): Promise<void> {
    try {
      const updated = await this.http.put<Post>(`${this.apiUrl}/${id}`, updatedPost).pipe(
        catchError((error: HttpErrorResponse) => {
          console.error(`Erreur lors de la mise à jour du post avec ID ${id}:`, error);
          throw error;
        })
      ).toPromise();
      if (updated) {
        this.loadPosts(); // Rafraîchir la liste après mise à jour
      } else {
        throw new Error('Le post mis à jour est undefined');
      }
    } catch (error) {
      console.error('Erreur lors de la mise à jour:', error);
      throw error;
    }
  }

  async deletePost(id: string): Promise<void> {
    try {
      await this.http.delete<void>(`${this.apiUrl}/${id}`).pipe(
        catchError((error: HttpErrorResponse) => {
          console.error(`Erreur lors de la suppression du post avec ID ${id}:`, error);
          throw error;
        })
      ).toPromise();
      this.loadPosts(); // Rafraîchir la liste après suppression
    } catch (error) {
      console.error('Erreur lors de la suppression:', error);
      throw error;
    }
  }

  toggleLike(postId: string): void {
    this.http.post<void>(`${this.apiUrl}/${postId}/like`, {}).pipe(
      catchError((error: HttpErrorResponse) => {
        console.error(`Erreur lors du toggle like pour le post avec ID ${postId}:`, error);
        return of(null);
      })
    ).subscribe(() => {
      this.loadPosts(); // Rafraîchir la liste après toggle like
    });
  }

  toggleFavorite(postId: string): void {
    this.http.post<void>(`${this.apiUrl}/${postId}/favorite`, {}).pipe(
      catchError((error: HttpErrorResponse) => {
        console.error(`Erreur lors du toggle favorite pour le post avec ID ${postId}:`, error);
        return of(null);
      })
    ).subscribe(() => {
      this.loadPosts(); // Rafraîchir la liste après toggle favorite
    });
  }

  getFavorites(): Observable<Post[]> {
    return this.favoritesSubject.asObservable();
  }

  private getFavoritesSync(): Post[] {
    return this.postsSubject.getValue().filter(p => p.isFavorite);
  }

  get favoritesCount$(): Observable<number> {
    return this.getFavorites().pipe(map(favorites => favorites.length));
  }

  setSearchQuery(query: string): void {
    if (!query.trim()) {
      this.filteredPostsSubject.next(this.postsSubject.getValue());
      return;
    }
    this.http.get<Post[]>(`${this.apiUrl}/search?query=${query}`).pipe(
      catchError((error: HttpErrorResponse) => {
        console.error('Erreur lors de la recherche:', error);
        this.filteredPostsSubject.next(this.postsSubject.getValue());
        return of([]);
      })
    ).subscribe(filteredPosts => {
      this.filteredPostsSubject.next(filteredPosts);
    });
  }

  resetSearch(): void {
    this.filteredPostsSubject.next(this.postsSubject.getValue());
  }

  async addComment(postId: string, comment: BlogComment): Promise<void> {
    try {
      const newComment = await this.http.post<any>(this.commentsApiUrl, { ...comment, postId, authorId: '1' }).pipe(
        catchError((error: HttpErrorResponse) => {
          console.error(`Erreur lors de l'ajout du commentaire pour le post avec ID ${postId}:`, error);
          throw error;
        })
      ).toPromise();
      if (newComment) {
        this.loadPosts(); // Rafraîchir la liste après ajout de commentaire
      } else {
        throw new Error('Le commentaire retourné est undefined');
      }
    } catch (error) {
      console.error('Erreur lors de l\'ajout du commentaire:', error);
      throw error;
    }
  }

  private loadComments(postId: string): Observable<BlogComment[]> {
    return this.http.get<any[]>(`${this.commentsApiUrl}/post/${postId}`).pipe(
      map(comments => comments.map(comment => ({
        id: comment.id,
        content: comment.content,
        authorUsername: comment.authorUsername,
        createdAt: comment.createdAt
      }))),
      catchError((error: HttpErrorResponse) => {
        console.error(`Erreur lors du chargement des commentaires pour le post avec ID ${postId}:`, error);
        return of([]);
      })
    );
  }
}
