import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-admin',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <div class="admin-container">
      <h1>Administration des articles</h1>
      <section class="posts-list">
        <h2>Articles existants</h2>

        <ng-container *ngIf="posts$ | async as posts; else loading">
          <div *ngIf="posts.length > 0; else noPosts" class="posts-grid">
            <div *ngFor="let post of posts; trackBy: trackById" class="post-card">
              <div class="post-image" [style.backgroundImage]="getImageUrl(post.imageUrl)"></div>
              <div class="post-info">
                <span class="category">{{ post.category || 'Sans catégorie' }}</span>
                <h3>{{ post.title || 'Titre indisponible' }}</h3>
                <p class="content">{{ (post.content | slice:0:100) || 'Contenu indisponible' }}...</p>
                <p class="meta">Par {{ post.author || 'Anonyme' }} le {{ post.createdAt | date:'mediumDate' }}</p>
                <div class="actions">
                  <a [routerLink]="['/edit-post', post.id]" class="edit-btn">
                    <i class="fas fa-edit"></i> Modifier
                  </a>
                  <button class="delete-btn" (click)="deletePost(post.id)">
                    <i class="fas fa-trash"></i> Supprimer
                  </button>
                </div>
              </div>
            </div>
          </div>
        </ng-container>

        <ng-template #noPosts>
          <p class="no-posts">Aucun article disponible pour le moment.</p>
        </ng-template>

        <ng-template #loading>
          <p class="loading">Chargement des articles...</p>
        </ng-template>
      </section>
    </div>
  `,
  styles: [/* ...tes styles inchangés, très bons... */]
})
export class AdminComponent implements OnInit {
  posts$: Observable<Post[]>;
  defaultImage = 'https://via.placeholder.com/300x200?text=No+Image';

  constructor(private postService: PostService) {}

  ngOnInit(): void {
    this.loadPosts();
  }

  loadPosts(): void {
    this.posts$ = this.postService.getPosts();
  }

  deletePost(id: number): void {
    if (confirm('Êtes-vous sûr de vouloir supprimer cet article ?')) {
      this.postService.deletePost(id).subscribe({
        next: () => this.loadPosts(),
        error: (err: unknown) => {
          console.error('Erreur lors de la suppression :', err);
        }
      });
    }
  }

  getImageUrl(imageUrl: string | undefined): string {
    return `url(${imageUrl || this.defaultImage})`;
  }

  trackById(index: number, post: Post): number {
    return post.id;
  }
}
