import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router, RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-post-form',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './post-form.component.html',
  styleUrls: ['./post-form.component.css']
})
export class PostFormComponent implements OnInit {
  post: Post = { 
    id: 0, 
    title: '', 
    content: '', 
    imageUrl: '', 
    author: '', 
    createdAt: '', 
    category: 'Peinture', 
    likes: 0, 
    isLiked: false, 
    isFavorite: false, 
    comments: [] 
  };
  isEditMode = false;
  post$: Observable<Post | undefined> | undefined;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private postService: PostService
  ) {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.isEditMode = true;
      this.post$ = this.postService.getPostById(+id);
    }
  }

  ngOnInit(): void {
    if (this.post$ && this.isEditMode) {
      this.post$.subscribe(post => {
        if (post) {
          this.post = { ...post };
        }
      });
    }
  }

  savePost(): void {
    if (this.post.title && this.post.content && this.post.imageUrl && this.post.author && this.post.category) {
      if (this.isEditMode) {
        this.postService.updatePost(this.post.id, this.post);
      } else {
        this.postService.addPost(this.post);
      }
      this.router.navigate(['/']);
    }
  }

  cancel(): void {
    this.router.navigate(['/']);
  }
}
