import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';

@Component({
  selector: 'app-admin',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <div class="admin-container">
      <h1>Administration des articles</h1>
      <div class="posts-list">
        <h2>Articles existants</h2>
        <div class="posts-grid">
          @for (post of posts; track post.id) {
            <div class="post-card">
              <div class="post-image" [style.backgroundImage]="'url(' + post.imageUrl + ')'"></div>
              <div class="post-info">
                <span class="category">{{ post.category }}</span>
                <h3>{{ post.title }}</h3>
                <p class="content">{{ post.content | slice:0:100 }}...</p>
                <p class="meta">Par {{ post.author }} le {{ post.createdAt | date:'mediumDate' }}</p>
                <div class="actions">
                  <a [routerLink]="['/edit-post', post.id]" class="edit-btn">
                    <i class="fas fa-edit"></i> Modifier
                  </a>
                  <button class="delete-btn" (click)="deletePost(post.id)">
                    <i class="fas fa-trash"></i> Supprimer
                  </button>
                </div>
              </div>
            </div>
          }
        </div>
      </div>
    </div>
  `,
  styles: [`
    .admin-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 40px 20px;
    }
    h1 {
      font-family: 'Playfair Display', serif;
      font-size: 2.5rem;
      color: #2C3E50;
      margin-bottom: 40px;
      text-align: center;
    }
    .posts-list {
      background: white;
      padding: 30px;
      border-radius: 12px;
      box-shadow: 0 4px 15px rgba(0,0,0,0.1);
    }
    h2 {
      font-family: 'Playfair Display', serif;
      color: #2C3E50;
      margin-bottom: 20px;
    }
    .posts-grid {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
      gap: 30px;
    }
    .post-card {
      border-radius: 12px;
      overflow: hidden;
      box-shadow: 0 4px 15px rgba(0,0,0,0.1);
      transition: transform 0.3s ease;
    }
    .post-card:hover {
      transform: translateY(-5px);
    }
    .post-image {
      height: 200px;
      background-size: cover;
      background-position: center;
    }
    .post-info {
      padding: 20px;
    }
    .category {
      display: inline-block;
      background: #D4A017;
      color: white;
      padding: 5px 10px;
      border-radius: 15px;
      font-size: 0.8rem;
      margin-bottom: 10px;
    }
    .post-info h3 {
      margin: 0;
      font-family: 'Playfair Display', serif;
      font-size: 1.3rem;
      color: #2C3E50;
    }
    .content {
      color: #7f8c8d;
      margin: 10px 0;
    }
    .meta {
      font-size: 0.9rem;
      color: #95a5a6;
      margin-bottom: 15px;
    }
    .actions {
      display: flex;
      gap: 10px;
    }
    .edit-btn, .delete-btn {
      flex: 1;
      padding: 10px;
      border-radius: 5px;
      cursor: pointer;
      transition: all 0.3s ease;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 5px;
    }
    .edit-btn {
      background: #D4A017;
      color: white;
      text-decoration: none;
    }
    .edit-btn:hover {
      background: #b88b14;
    }
    .delete-btn {
      background: #E74C3C;
      color: white;
      border: none;
    }
    .delete-btn:hover {
      background: #c0392b;
    }
    @media (max-width: 768px) {
      .posts-grid {
        grid-template-columns: 1fr;
      }
    }
  `]
})
export class AdminComponent implements OnInit {
  posts: Post[] = [];

  constructor(private postService: PostService) {}

  ngOnInit() {
    this.postService.getPosts().subscribe(posts => {
      this.posts = posts;
    });
  }

  deletePost(id: number) {
    if (confirm('Êtes-vous sûr de vouloir supprimer cet article ?')) {
      this.postService.deletePost(id);
    }
  }
}
