import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { Observable, of } from 'rxjs';
import { catchError } from 'rxjs/operators';

@Component({
  selector: 'app-admin',
  standalone: true,
  imports: [CommonModule, RouterModule, FormsModule],
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {
  posts$: Observable<Post[]>;
  defaultImage = 'https://via.placeholder.com/300x200?text=Image+non+disponible';
  showForm: boolean = false;
  isLoading: boolean = false;
  newPost: Post = {
    id: 0,
    title: '',
    content: '',
    imageUrl: '',
    author: '',
    createdAt: new Date().toISOString(),
    category: 'Peinture',
    likes: 0,
    isLiked: false,
    isFavorite: false,
    comments: [],
    views: 0
  };

  constructor(private postService: PostService) {
    this.posts$ = this.postService.getPosts().pipe(
      catchError((error: unknown) => {
        console.error('Erreur lors du chargement des articles:', error);
        return of([]);
      })
    );
  }

  ngOnInit(): void {}

  toggleForm(): void {
    this.showForm = !this.showForm;
    if (!this.showForm) {
      this.resetForm();
    }
  }

  async addPost(): Promise<void> {
    if (this.newPost.title && this.newPost.content && this.newPost.imageUrl && this.newPost.author && this.newPost.category) {
      this.isLoading = true;
      try {
        await this.postService.addPost(this.newPost);
        this.resetForm();
        this.showForm = false;
      } catch (error) {
        console.error('Erreur lors de l\'ajout:', error);
        alert('Une erreur est survenue lors de l\'ajout');
      } finally {
        this.isLoading = false;
      }
    }
  }

  resetForm(): void {
    this.newPost = {
      id: 0,
      title: '',
      content: '',
      imageUrl: '',
      author: '',
      createdAt: new Date().toISOString(),
      category: 'Peinture',
      likes: 0,
      isLiked: false,
      isFavorite: false,
      comments: [],
      views: 0
    };
  }

  trackById(index: number, post: Post): number {
    return post.id;
  }

  getImageUrl(imageUrl: string | undefined): string {
    return `url(${imageUrl || this.defaultImage})`;
  }

  async deletePost(id: number): Promise<void> {
    if (confirm('Êtes-vous sûr de vouloir supprimer cet article ?')) {
      this.isLoading = true;
      try {
        await this.postService.deletePost(id);
      } catch (error) {
        console.error('Erreur lors de la suppression:', error);
        alert('Une erreur est survenue lors de la suppression');
      } finally {
        this.isLoading = false;
      }
    }
  }
}
