import { Component } from '@angular/core';
import { RouterModule } from '@angular/router';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { PostService } from './services/post.service';
import { BehaviorSubject } from 'rxjs';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterModule, CommonModule, FormsModule],
  template: `
    <header class="main-header">
      <div class="header-content">
        <div class="logo-section">
          <a [routerLink]="['/']" class="logo">
            <i class="fas fa-paint-brush"></i>
            <span>ArtBlog</span>
          </a>
        </div>

        <div class="search-bar">
          <input 
            type="text" 
            [(ngModel)]="searchQuery" 
            (keyup)="onSearch()"
            placeholder="Rechercher des articles..."
          >
          <i class="fas fa-search"></i>
        </div>

        <nav class="main-nav">
          <a [routerLink]="['/']" 
             [routerLinkActive]="['active']" 
             [routerLinkActiveOptions]="{exact: true}">
            <i class="fas fa-home"></i>
            <span>Accueil</span>
          </a>
          <a [routerLink]="['/favorites']" 
             [routerLinkActive]="['active']">
            <i class="fas fa-heart"></i>
            <span>Favoris</span>
            <span class="badge" *ngIf="favoritesCount$ | async as count">{{ count }}</span>
          </a>
          <a [routerLink]="['/new-post']" 
             [routerLinkActive]="['active']">
            <i class="fas fa-plus"></i>
            <span>Nouvel Article</span>
          </a>
          <a *ngIf="isAdmin" 
             [routerLink]="['/admin']" 
             [routerLinkActive]="['active']">
            <i class="fas fa-cog"></i>
            <span>Admin</span>
          </a>
        </nav>
      </div>
    </header>

    <main>
      <router-outlet></router-outlet>
    </main>

    <footer class="main-footer">
      <div class="footer-content">
        <div class="footer-section">
          <h3>À propos</h3>
          <p>ArtBlog est un espace dédié à l'art, où vous pouvez découvrir des articles inspirants et partager vos idées.</p>
        </div>

        <div class="footer-section">
          <h3>Liens rapides</h3>
          <ul>
            <li><a routerLink="/about">À propos</a></li>
            <li><a routerLink="/contact">Contact</a></li>
            <li><a routerLink="/terms">Conditions d'utilisation</a></li>
            <li><a routerLink="/privacy">Politique de confidentialité</a></li>
          </ul>
        </div>

        <div class="footer-section">
          <h3>Contact</h3>
          <p>
            <i class="fas fa-envelope"></i> <span>contact@artblog.com</span><br>
            <i class="fas fa-phone"></i> +33 1 23 45 67 89<br>
            <i class="fas fa-map-marker-alt"></i> Paris, France
          </p>
        </div>

        <div class="footer-section">
          <h3>Suivez-nous</h3>
          <div class="social-links">
            <a href="#"><i class="fab fa-facebook"></i></a>
            <a href="#"><i class="fab fa-instagram"></i></a>
            <a href="#"><i class="fab fa-twitter"></i></a>
            <a href="#"><i class="fab fa-linkedin"></i></a>
          </div>
        </div>
      </div>

      <div class="footer-bottom">
        <p>© 2025 ArtBlog. Tous droits réservés.</p>
      </div>
    </footer>
  `,
  styles: [`
    :host {
      display: flex;
      flex-direction: column;
      min-height: 100vh;
    }

    main {
      flex: 1;
      padding: 20px;
      background-color: #F8F1E9;
    }

    .main-header {
      background-color: #ffffff;
      box-shadow: 0 2px 8px rgba(0,0,0,0.1);
      padding: 15px 0;
      position: sticky;
      top: 0;
      z-index: 1000;
    }

    .header-content {
      max-width: 1200px;
      margin: 0 auto;
      padding: 0 20px;
      display: flex;
      align-items: center;
      justify-content: space-between;
      gap: 20px;
    }

    .logo-section {
      flex: 0 0 auto;
    }

    .logo {
      display: flex;
      align-items: center;
      gap: 10px;
      text-decoration: none;
      color: #D4A017;
      font-family: 'Playfair Display', serif;
      font-size: 1.8rem;
      font-weight: 700;
    }

    .logo i {
      font-size: 1.8rem;
    }

    .search-bar {
      flex: 1;
      max-width: 500px;
      position: relative;
    }

    .search-bar input {
      width: 100%;
      padding: 10px 40px 10px 15px;
      border: 2px solid #e9ecef;
      border-radius: 25px;
      font-size: 1rem;
      transition: border-color 0.3s ease;
    }

    .search-bar input:focus {
      outline: none;
      border-color: #D4A017;
    }

    .search-bar i {
      position: absolute;
      right: 15px;
      top: 50%;
      transform: translateY(-50%);
      color: #6c757d;
    }

    .main-nav {
      display: flex;
      gap: 20px;
      align-items: center;
    }

    .main-nav a {
      display: flex;
      flex-direction: column;
      align-items: center;
      text-decoration: none;
      color: #2C3E50;
      font-size: 0.9rem;
      position: relative;
      padding: 5px 10px;
      transition: color 0.3s ease;
    }

    .main
