    }

    .like-btn, .favorite-btn {
      padding: 10px 20px;
      border-radius: 25px;
      border: none;
      cursor: pointer;
      display: flex;
      align-items: center;
      gap: 8px;
      font-size: 1rem;
      transition: all 0.3s ease;
    }

