})
export class PostDetailComponent implements OnInit {
  post: Post | undefined;
  newComment: Comment = { id: 0, content: '', author: '', createdAt: '' };

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private postService: PostService
  ) {}

  ngOnInit() {
    this.route.params.subscribe(params => {
      const id = +params['id'];
      this.postService.getPostById(id).subscribe(post => {
        this.post = post;
      });
    });
  }

  toggleLike(postId: number) {
    this.postService.toggleLike(postId);
  }

  toggleFavorite(postId: number) {
    this.postService.toggleFavorite(postId);
  }

  addComment() {
    if (this.post && this.newComment.content && this.newComment.author) {
      this.postService.addComment(this.post.id, this.newComment);
      this.newComment = { id: 0, content: '', author: '', createdAt: '' };
    }
  }

  goBack() {
    this.router.navigate(['/']);
  }
}
