import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { Observable, of, Subject } from 'rxjs';
import { catchError, tap } from 'rxjs/operators';
import { lastValueFrom } from 'rxjs'; // Import lastValueFrom

@Component({
  selector: 'app-admin',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './admin.component.html',
  styleUrls: ['./admin.component.css']
})
export class AdminComponent implements OnInit {
  posts$: Observable<Post[]>;
  defaultImage = 'https://via.placeholder.com/300x200?text=Image+non+disponible';
  isLoading = true;
  errorMessage: string | null = null;
  showDeleteModal = false;
  postToDelete: number | null = null;
  notificationMessage: string | null = null;
  notificationType: 'success' | 'error' | null = null;
  previewPost: Post | null = null;

  private refreshSubject = new Subject<void>();

  constructor(private postService: PostService) {
    this.posts$ = this.loadPosts();
  }

  ngOnInit(): void {
    // Trigger initial load
    this.refreshPosts();
  }

  loadPosts(): Observable<Post[]> {
    return this.postService.getPosts().pipe(
      tap(() => {
        this.isLoading = false;
        this.errorMessage = null;
      }),
      catchError((error: unknown) => {
        console.error('Erreur lors du chargement des articles:', error);
        this.isLoading = false;
        this.errorMessage = 'Impossible de charger les articles. Veuillez réessayer.';
        return of([]);
      })
    );
  }

  trackById(index: number, post: Post): number {
    return post.id;
  }

  getImageUrl(imageUrl: string | undefined): string {
    return `url(${imageUrl || this.defaultImage})`;
  }

  refreshPosts(): void {
    this.isLoading = true;
    this.errorMessage = null;
    this.posts$ = this.loadPosts();
  }

  openDeleteModal(postId: number): void {
    this.postToDelete = postId;
    this.showDeleteModal = true;
  }

  closeDeleteModal(): void {
    this.showDeleteModal = false;
    this.postToDelete = null;
  }

  async confirmDelete(): Promise<void> {
    if (this.postToDelete !== null) {
      try {
        await lastValueFrom(this.postService.deletePost(this.postToDelete)); // Convert Observable to Promise
        this.showNotification('Article supprimé avec succès !', 'success');
        this.refreshPosts();
      } catch (error) {
        console.error('Erreur lors de la suppression:', error);
        this.showNotification('Erreur lors de la suppression de l\'article.', 'error');
      } finally {
        this.closeDeleteModal();
      }
    }
  }

  showNotification(message: string, type: 'success' | 'error'): void {
    this.notificationMessage = message;
    this.notificationType = type;
    setTimeout(() => {
      this.notificationMessage = null;
      this.notificationType = null;
    }, 3000);
  }

  openPreview(post: Post): void {
    this.previewPost = post;
  }

  closePreview(): void {
    this.previewPost = null;
  }
}
