import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';

@Component({
  selector: 'app-post-list',
  standalone: true,
  imports: [CommonModule, RouterModule],
  template: `
    <div class="container">
      <h1>Articles sur l'Art</h1>
      <div class="posts-grid">
        @for (post of posts; track post.id) {
          <div class="post-card">
            <div class="post-image" [style.backgroundImage]="'url(' + post.imageUrl + ')'"></div>
            <div class="post-info">
              <span class="category">{{ post.category }}</span>
              <h3>{{ post.title }}</h3>
              <p class="content">{{ post.content | slice:0:100 }}...</p>
              <p class="meta">Par {{ post.author }} le {{ post.createdAt | date:'mediumDate' }}</p>
              <div class="actions">
                <button class="like-btn" (click)="toggleLike(post.id)">
                  <i class="fas fa-heart" [ngClass]="post.isLiked ? 'liked' : ''"></i>
                  {{ post.likes }}
                </button>
                <button class="favorite-btn" (click)="toggleFavorite(post.id)">
                  <i class="fas fa-star" [ngClass]="post.isFavorite ? 'favorited' : ''"></i>
                </button>
                <a [routerLink]="['/post', post.id]" class="view-btn">
                  Lire l'article
                </a>
              </div>
            </div>
          </div>
        }
      </div>
      @if (posts.length === 0) {
        <div class="empty-state">
          <i class="fas fa-paint-brush"></i>
          <h2>Aucun article trouvé</h2>
          <p>Commencez à écrire un nouvel article pour partager votre passion pour l'art.</p>
          <a routerLink="/new-post" class="start-post">
            Nouvel Article
          </a>
        </div>
      }
    </div>
  `,
  styles: [`
    .container {
      padding: 40px 20px;
      max-width: 1200px;
      margin: 0 auto;
    }
    h1 {
      font-family: 'Playfair Display', serif;
      font-size: 2.5rem;
      color: #2C3E50;
      margin-bottom: 40px;
      text-align: center;
    }
    .posts-grid {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
      gap: 30px;
    }
    .post-card {
      background: white;
      border-radius: 12px;
      overflow: hidden;
      box-shadow: 0 4px 15px rgba(0,0,0,0.1);
      transition: transform 0.3s ease, box-shadow 0.3s ease;
    }
    .post-card:hover {
      transform: translateY(-5px);
      box-shadow: 0 6px 20px rgba(0,0,0,0.15);
    }
    .post-image {
      height: 200px;
      background-size: cover;
      background-position: center;
    }
    .post-info {
      padding: 20px;
    }
    .category {
      display: inline-block;
      background: #D4A017;
      color: white;
      padding: 5px 10px;
      border-radius: 15px;
      font-size: 0.8rem;
      margin-bottom: 10px;
    }
    .post-info h3 {
      margin: 0;
      font-family: 'Playfair Display', serif;
      font-size: 1.3rem;
      color: #2C3E50;
    }
    .content {
      color: #7f8c8d;
      margin: 10px 0;
      line-height: 1.5;
    }
    .meta {
      font-size: 0.9rem;
      color: #95a5a6;
      margin-bottom: 15px;
    }
    .actions {
      display: flex;
      gap: 10px;
      align-items: center;
    }
    .like-btn, .favorite-btn {
      background: none;
      border: none;
      cursor: pointer;
      display: flex;
      align-items: center;
      gap: 5px;
      color: #95a5a6;
      transition: color 0.3s ease;
    }
    .like-btn i, .favorite-btn i {
      font-size: 1.2rem;
    }
    .like-btn:hover, .favorite-btn:hover {
      color: #E74C3C;
    }
    .like-btn i.liked {
      color: #E74C3C;
    }
    .favorite-btn i.favorited {
      color: #D4A017;
    }
    .view-btn {
      flex: 1;
      padding: 10px;
      border-radius: 5px;
      text-align: center;
      background: #D4A017;
      color: white;
      text-decoration: none;
      transition: all 0.3s ease;
    }
    .view-btn:hover {
      background: #b88b14;
    }
    .empty-state {
      text-align: center;
      padding: 60p
