                      <i class="fas fa-trash-alt"></i> Supprimer
                    </button>
                  </div>
                </div>
              </article>
            } @empty {
              <p class="no-posts">Aucun article pour le moment.</p>
            }
          </div>
        } @else {
          <p class="loading">‚è≥ Chargement en cours...</p>
        }
      </section>
    </div>
  `,
  styles: [`
    :host {
      display: block;
      font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
      background: #f9f9f9;
      min-height: 100vh;
    }

    .admin-container {
      max-width: 1200px;
      margin: 0 auto;
      padding: 2rem 1rem;
    }

    h1 {
      font-size: 2.5rem;
      color: #34495e;
      margin-bottom: 2rem;
      text-align: center;
    }

    .posts-list {
      background: #fff;
      padding: 2rem;
      border-radius: 1rem;
      box-shadow: 0 4px 16px rgba(0, 0, 0, 0.08);
    }

    h2 {
      font-size: 1.75rem;
      color: #2c3e50;
      margin-bottom: 1.25rem;
      text-align: center;
    }

    .posts-grid {
      display: grid;
      grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
      gap: 1.5rem;
    }

    .post-card {
      background: #fff;
      border-radius: 1rem;
      overflow: hidden;
      box-shadow: 0 2px 12px rgba(0,0,0,0.1);
      transition: transform 0.3s ease, box-shadow 0.3s ease;
    }

    .post-card:hover {
      transform: translateY(-5px);
      box-shadow: 0 6px 20px rgba(0,0,0,0.15);
    }

    .post-image {
      height: 180px;
      background-size: cover;
      background-position: center;
      background-color: #e0e0e0;
    }

    .post-info {
      padding: 1rem;
    }

    .category {
      background: #e67e22;
      color: #fff;
      font-size: 0.75rem;
      padding: 0.3rem 0.6rem;
      border-radius: 999px;
      display: inline-block;
      margin-bottom: 0.5rem;
    }

    .post-info h3 {
      font-size: 1.2rem;
      color: #2c3e50;
      margin: 0 0 0.5rem;
    }

    .content {
      font-size: 0.95rem;
      color: #7f8c8d;
      margin-bottom: 0.75rem;
    }

    .meta {
      font-size: 0.85rem;
      color: #95a5a6;
      margin-bottom: 1rem;
    }

    .actions {
      display: flex;
      justify-content: space-between;
      gap: 0.5rem;
    }

    .edit-btn, .delete-btn {
      flex: 1;
      padding: 0.5rem;
      font-size: 0.9rem;
      border-radius: 6px;
      border: none;
      cursor: pointer;
      display: flex;
      align-items: center;
      justify-content: center;
      gap: 0.3rem;
