import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ActivatedRoute, Router } from '@angular/router';
import { FormsModule } from '@angular/forms';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { BlogComment } from '../../models/comment.model';

@Component({
  selector: 'app-post-detail',
  standalone: true,
  imports: [CommonModule, FormsModule],
  template: `
    @if (post) {
      <div class="post-detail">
        <div class="post-header">
          <span class="category">{{ post.category }}</span>
          <h1>{{ post.title }}</h1>
          <p class="meta">Par {{ post.author }} le {{ post.createdAt | date:'mediumDate' }}</p>
          <div class="post-image" [style.backgroundImage]="'url(' + post.imageUrl + ')'"></div>
        </div>
        <div class="post-content">
          <p>{{ post.content }}</p>
        </div>
        <div class="post-actions">
          <button class="like-btn" (click)="toggleLike(post.id)">
            <i class="fas fa-heart" [ngClass]="post.isLiked ? 'liked' : ''"></i>
            {{ post.likes }} J'aime
          </button>
          <button class="favorite-btn" (click)="toggleFavorite(post.id)">
            <i class="fas fa-star" [ngClass]="post.isFavorite ? 'favorited' : ''"></i>
            {{ post.isFavorite ? 'Retirer des favoris' : 'Ajouter aux favoris' }}
          </button>
        </div>
        <div class="comments-section">
          <h2>Commentaires ({{ post.comments.length }})</h2>
          @for (comment of post.comments; track comment.id) {
            <div class="comment">
              <p class="comment-meta">Par {{ comment.author }} le {{ comment.createdAt | date:'mediumDate' }}</p>
              <p>{{ comment.content }}</p>
            </div>
          }
          @if (post.comments.length === 0) {
            <p>Aucun commentaire pour le moment.</p>
          }
          <div class="comment-form">
            <h3>Ajouter un commentaire</h3>
            <form (submit)="addComment()">
              <div class="form-group">
                <label for="comment-content">Votre commentaire</label>
                <textarea 
                  id="comment-content" 
                  [(ngModel)]="newComment.content" 
                  name="content" 
                  required
                ></textarea>
              </div>
              <div class="form-group">
                <label for="comment-author">Votre nom</label>
                <input 
                  type="text" 
                  id="comment-author" 
                  [(ngModel)]="newComment.author" 
                  name="author" 
                  required
                >
              </div>
              <button type="submit" class="submit-btn">Publier</button>
            </form>
          </div>
        </div>
      </div>
    } @else {
      <div class="error">
        <h2>Article non trouv√©</h2>
        <button (click)="goBack()">Retour aux articles</button>
      </div>
    }
  `,
  styles: [`
    .post-detail {
      max-width: 800px;
      margin: 40px auto;
      padding: 0 20px;
    }

    .post-header {
      text-align: center;
      margin-bottom: 40px;
    }

    .category {
      display: inline-block;
      background: #D4A017;
      color: white;
      padding: 5px 15px;
      border-radius: 15px;
      font-size: 0.9rem;
      margin-bottom: 15px;
    }

    .post-header h1 {
      font-family: 'Playfair Display', serif;
      font-size: 2.8rem;
      color: #2C3E50;
      margin: 0 0 15px 0;
    }

    .meta {
      font-size: 1.1rem;
      color: #95a5a6;
      margin-bottom: 20px;
    }

    .post-image {
      height: 400px;
      background-size: cover;
      background-position: center;
      border-radius: 12px;
      box-shadow: 0 4px 15px rgba(0,0,0,0.1);
    }

    .post-content {
      background: white;
      padding: 30px;
      border-radius: 12px;
      box-shadow: 0 4px 15px rgba(0,0,0,0.1);
      margin-bottom: 30px;
      line-height: 1.8;
      color:
