import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { Observable } from 'rxjs';
import { delay } from 'rxjs/operators';

@Component({
  selector: 'app-post-list',
  standalone: true,
  imports: [CommonModule, RouterModule],
  templateUrl: './post-list.component.html',
  styleUrls: ['./post-list.component.css']
})
export class PostListComponent implements OnInit {
  posts$: Observable<Post[]>;
  defaultImage = 'https://via.placeholder.com/300x200?text=Image+non+disponible';
  isLoading = true; // Add loading state

  constructor(private postService: PostService) {
    // Simulate a delay to show loading spinner (remove delay in a real app with API)
    this.posts$ = this.postService.getPosts().pipe(
      delay(1000) // Simulate network delay
    );
  }

  ngOnInit(): void {
    // Set loading to false once posts are loaded
    this.posts$.subscribe(() => {
      this.isLoading = false;
    });
  }

  toggleLike(postId: number): void {
    this.postService.toggleLike(postId);
  }

  toggleFavorite(postId: number): void {
    this.postService.toggleFavorite(postId);
  }

  trackById(index: number, post: Post): number {
    return post.id;
  }

  getImageUrl(imageUrl: string | undefined): string {
    return imageUrl || this.defaultImage;
  }
}
