import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router, RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-post-form',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './post-form.component.html',
  styleUrls: ['./post-form.component.css']
})
export class PostFormComponent implements OnInit {
  post: Post = { 
    id: 0, 
    title: '', 
    content: '', 
    imageUrl: '', 
    authorUsername: '', 
    createdAt: '', 
    category: 'Peinture', 
    likes: 0, 
    isLiked: false, 
    isFavorite: false, 
    comments: [],
    views: 0
  };
  isEditMode = false;
  post$: Observable<Post | undefined> | undefined;
  isSubmitting = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private postService: PostService
  ) {
    const id = this.route.snapshot.paramMap.get('id');
    if (id) {
      this.isEditMode = true;
      this.post$ = this.postService.getPostById(+id);
    }
  }

  ngOnInit(): void {
    if (this.post$ && this.isEditMode) {
      this.post$.subscribe(post => {
        if (post) {
          this.post = { ...post };
        }
      });
    }
  }

  async savePost(): Promise<void> {
    if (this.isFormValid() && !this.isSubmitting) {
      this.isSubmitting = true;
      try {
        if (this.isEditMode) {
          await this.postService.updatePost(this.post.id, this.post);
        } else {
          this.post.createdAt = new Date().toISOString();
          await this.postService.addPost(this.post);
        }
        this.router.navigate(['/']);
      } catch (error) {
        console.error('Error saving post:', error);
      } finally {
        this.isSubmitting = false;
      }
    }
  }

  cancel(): void {
    this.router.navigate(['/']);
  }

  private isFormValid(): boolean {
    return !!this.post.title && 
           !!this.post.content && 
           !!this.post.imageUrl && 
           !!this.post.authorUsername && 
           !!this.post.category;
  }
}
