        <div class="posts-grid" *ngIf="filteredPosts.length > 0; else noPosts">
          @for (post of filteredPosts; track post.id) {
            <div class="post-card" @cardAnimation>
              <div 
                class="post-image" 
                [style.backgroundImage]="'url(' + (post.imageUrl || defaultImage) + ')'"
                (click)="viewPost(post.id)"
              ></div>
              <div class="post-info">
                <span class="category" [ngStyle]="{'background-color': getCategoryColor(post.category)}">
                  {{ post.category }}
                </span>
                <h3>{{ post.title }}</h3>
                <p class="content">{{ post.content | slice:0:120 }}...</p>
                <p class="meta">
                  Par {{ post.author }} 
                  <span class="date">• {{ post.createdAt | date:'mediumDate' }}</span>
                </p>
                <div class="actions">
                  <a [routerLink]="['/edit-post', post.id]" class="edit-btn">
                    <i class="fas fa-edit"></i> Modifier
                  </a>
                  <button class="delete-btn" (click)="deletePost(post.id)">
                    <i class="fas fa-trash"></i> Supprimer
                  </button>
                </div>
              </div>
            </div>
          }
        </div>

        <ng-template #noPosts>
          <div class="no-posts">
            <i class="fas fa-folder-open"></i>
            <p>Aucun article trouvé</p>
          </div>
        </ng-template>
      </div>

      <ng-template #loadingTemplate>
        <div class="loading">
          <div class="spinner"></div>
