  animations: [
    trigger('cardAnimation', [
      transition(':enter', [
        style({ opacity: 0, transform: 'translateY(20px)' }),
        animate('300ms ease-out', style({ opacity: 1, transform: 'translateY(0)' }))
      ])
    ])
  ]
})
export class AdminComponent implements OnInit {
  posts: Post[] = [];
  filteredPosts: Post[] = [];
  loading = true;
  defaultImage = 'https://via.placeholder.com/400x220';
  private originalPosts: Post[] = [];

  constructor(private postService: PostService) {}

  ngOnInit() {
    this.loadPosts();
  }

  loadPosts() {
    this.loading = true;
    this.postService.getPosts()
      .pipe(
        catchError(error => {
          console.error('Error loading posts:', error);
          this.loading = false;
          return of([]);
        })
      )
      .subscribe(posts => {
        this.posts = posts;
        this.filteredPosts = [...posts];
        this.originalPosts = [...posts];
        this.loading = false;
      });
  }

  filterPosts(event: Event) {
    const searchTerm = (event.target as HTMLInputElement).value.toLowerCase();
    this.filteredPosts = this.originalPosts.filter(post =>
      post.title.toLowerCase().includes(searchTerm) ||
      post.content.toLowerCase().includes(searchTerm) ||
      post.author.toLowerCase().includes(searchTerm)
    );
  }

  deletePost(id: number) {
    if (confirm('Êtes-vous sûr de vouloir supprimer cet article ? Cette action est irréversible.')) {
      this.postService.deletePost(id).subscribe({
        next: () => {
          this.posts = this.posts.filter(post => post.id !== id);
          this.filteredPosts = this.filteredPosts.filter(post => post.id !== id);
          this.originalPosts = this.originalPosts.filter(post => post.id !== id);
        },
        error: (error) => {
          console.error('Error deleting post:', error);
          alert('Une erreur est survenue lors de la suppression');
        }
      });
    }
  }

  viewPost(id: number) {
    // Implement view post logic here (e.g., open in new tab or navigate)
    console.log('Viewing post:', id);
  }

  getCategoryColor(category: string): string {
    const colors = {
      'tech': '#3498db',
      'news': '#e74c3c',
      'blog': '#2ecc71',
      'other': '#9b59b6'
    };
    return colors[category.toLowerCase()] || '#7f8c8d';
  }
}
