// post-detail.component.ts
import { Component, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { ActivatedRoute, Router, RouterModule } from '@angular/router';
import { PostService } from '../../services/post.service';
import { Post } from '../../models/post.model';
import { BlogComment } from '../../models/comment.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-post-detail',
  standalone: true,
  imports: [CommonModule, FormsModule, RouterModule],
  templateUrl: './post-detail.component.html',
  styleUrls: ['./post-detail.component.css']
})
export class PostDetailComponent implements OnInit {
  post$: Observable<Post | undefined>;
  newComment: BlogComment = { id: 0, content: '', author: '', createdAt: '' };
  defaultImage = 'https://via.placeholder.com/800x400?text=Image+non+disponible';
  isSubmitting = false;

  constructor(
    private route: ActivatedRoute,
    private router: Router,
    private postService: PostService
  ) {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.post$ = this.postService.getPostById(id);
  }

  ngOnInit(): void {
    this.post$.subscribe(post => {
      if (post) {
        this.postService.incrementViews(post.id);
      }
    });
  }

  toggleLike(postId: number): void {
    this.postService.toggleLike(postId);
  }

  toggleFavorite(postId: number): void {
    this.postService.toggleFavorite(postId);
  }

  async addComment(postId: number): Promise<void> {
    if (this.newComment.content && this.newComment.author && !this.isSubmitting) {
      this.isSubmitting = true;
      try {
        await this.postService.addComment(postId, {
          ...this.newComment,
          createdAt: new Date().toISOString()
        });
        this.newComment = { id: 0, content: '', author: '', createdAt: '' };
      } catch (error) {
        console.error('Error adding comment:', error);
      } finally {
        this.isSubmitting = false;
      }
    }
  }

  goBack(): void {
    this.router.navigate(['/']);
  }

  getImageUrl(imageUrl: string | undefined): string {
    return imageUrl || this.defaultImage;
  }

  trackById(index: number, comment: BlogComment): number {
    return comment.id;
  }
}
