import { Component, OnInit, OnDestroy } from '@angular/core';
import { RouterModule } from '@angular/router';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms';
import { PostService } from './services/post.service';
import { NotificationService, Notification } from './services/notification.service';
import { Subscription } from 'rxjs';
import { Router } from '@angular/router';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterModule, CommonModule, FormsModule],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit, OnDestroy {
  searchQuery: string = '';
  favoritesCount$ = this.postService.favoritesCount$;
  notificationCount$ = this.notificationService.notificationCount$;
  isAdmin: boolean = true;
  email: string = 'contact@artblog.com';
  phone: string = '+33 1 23 45 67 89';
  location: string = 'Paris, France';
  showNotifications: boolean = false;
  notifications: Notification[] = [];
  private notificationSubscription: Subscription;

  constructor(
    private postService: PostService,
    private notificationService: NotificationService,
    private router: Router
  ) {
    this.notificationSubscription = this.notificationService.notifications$.subscribe(
      notifications => {
        this.notifications = notifications;
      }
    );
  }

  ngOnInit(): void {}

  ngOnDestroy(): void {
    this.notificationSubscription.unsubscribe();
  }

  onSearch(): void {
    this.postService.setSearchQuery(this.searchQuery);
  }

  onSearchFocus(): void {
    // Optional: Add logic for search focus (e.g., logging or UI state)
  }

  onSearchBlur(): void {
    // Optional: Add logic for search blur (e.g., logging or UI state)
  }

  clearSearch(): void {
    this.searchQuery = '';
    this.postService.setSearchQuery('');
  }

  toggleNotifications(): void {
    this.showNotifications = !this.showNotifications;
  }

  viewPost(notification: Notification): void {
    this.notificationService.markAsRead(notification.id);
    this.router.navigate(['/post', notification.postId]);
    this.showNotifications = false;
  }

  clearNotifications(): void {
    this.notificationService.clearNotifications();
  }
}
