import { Component, OnInit } from '@angular/core';
import { RouterOutlet, Router } from '@angular/router';
import { CommonModule } from '@angular/common';
import { FormsModule } from '@angular/forms'; // Import FormsModule for ngModel
import { RouterModule } from '@angular/router'; // Import RouterModule for routerLink
import { NotificationService } from './services/notification.service';
import { PostService } from './services/post.service';
import { Notification } from './models/notification.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [RouterOutlet, CommonModule, FormsModule, RouterModule], // Add FormsModule and RouterModule
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  notificationCount$: Observable<number>;
  favoritesCount$: Observable<number>;
  notifications: Notification[] = [];
  showNotifications: boolean = false;
  searchQuery: string = '';
  isAdmin: boolean = false; // You can set this dynamically based on user role
  email: string = 'artblog@example.com'; // Static contact info (replace with actual data)
  phone: string = '+123 456 7890';
  location: string = 'Paris, France';

  constructor(
    private notificationService: NotificationService,
    private postService: PostService,
    private router: Router
  ) {
    this.notificationCount$ = this.notificationService.getNotificationCount();
    this.favoritesCount$ = this.postService.favoritesCount$;
    this.notificationService.getNotifications().subscribe(notifications => {
      this.notifications = notifications;
    });
  }

  ngOnInit(): void {
    // Check if the user is an admin (this is a placeholder; implement actual auth logic)
    this.isAdmin = false; // Replace with actual logic, e.g., checking user role from a service
  }

  onSearch(): void {
    this.postService.setSearchQuery(this.searchQuery);
  }

  onSearchFocus(): void {
    // Add logic if needed when search input is focused
  }

  onSearchBlur(): void {
    // Add logic if needed when search input loses focus
  }

  clearSearch(): void {
    this.searchQuery = '';
    this.postService.setSearchQuery('');
  }

  toggleNotifications(): void {
    this.showNotifications = !this.showNotifications;
  }

  clearNotifications(): void {
    this.notifications = [];
    this.notificationService.getNotifications().subscribe(notifications => {
      this.notifications = notifications.filter(n => !n.read);
    });
  }

  viewPost(notification: Notification): void {
    // Assuming the notification message contains the post title
    // You may need to adjust this logic based on how post IDs are stored in notifications
    const postTitle = notification.message.replace('Nouvel article créé : ', '');
    this.postService.getPosts().subscribe(posts => {
      const post = posts.find(p => p.title === postTitle);
      if (post) {
        this.router.navigate(['/post', post.id]);
        this.showNotifications = false;
      }
    });
  }
}
