                <div class="post-meta">
                  <span class="meta-item">
                    <fa-icon [icon]="faUser"></fa-icon> {{ post.author }}
                  </span>
                  <span class="meta-item">
                    <fa-icon [icon]="faCalendarAlt"></fa-icon> {{ post.createdAt | date:'mediumDate' }}
                  </span>
                  <span class="meta-item">
                    <fa-icon [icon]="post.isLiked ? faHeart : farHeart" [ngClass]="{'liked': post.isLiked}"></fa-icon> 
                    {{ post.likes }}
                  </span>
                </div>

                <div class="actions">
                  <a [routerLink]="['/edit-post', post.id]" class="edit-btn">
                    <fa-icon [icon]="faEdit"></fa-icon> Modifier
                  </a>
                  <button class="delete-btn" (click)="deletePost(post.id)">
                    <fa-icon [icon]="faTrashAlt"></fa-icon> Supprimer
                  </button>
                </div>
              </div>
            </div>
          }
        </div>
      </div>
    </div>
  `,
  styles: [`
